    var country_names_array = country_names.split(',');

    var arr = $("#custom-table tbody tr");
    var arr_country_code = '';
    var arr_country_name = '';
    var arr_dictionary = [];
    for (var i = 0, l=arr.length; i < l; i++) {
      item = $(arr[i]);

      arr_country_name = item.find('td:nth-child(3)').html();
      arr_country_code = item.find('td:nth-child(4)').html();
      
      arr_country_name = $.trim(arr_country_name);
      arr_country_code = $.trim(arr_country_code);

      arr_dictionary[arr_country_code] = arr_country_name;
      // console.log(arr_country_name);
      // console.log(arr_country_code);
    }


    var output_array = [];
    var country_names_array_code = '';

    for (var i = 0, l=country_names_array.length; i < l; i++) {
      country_names_array_code = country_names_array[i];
      arr_country_name = arr_dictionary[country_names_array_code];
      //console.log(country_names_array_code);
      //console.log(arr_country_name);
      output_array[i] = arr_country_name;
    }


    var output_str = output_array.join(',');
    $('#output-txt').val(output_str);

    
  });
</script>





